mapper.sql:
	WHERE is_delete = 0

        AND id  = #{rid,jdbcType=INTEGER}


        AND goods_id = #{goodsId,jdbcType=INTEGER}


        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND start_time = #{startTime,jdbcType=TIMESTAMP}


        AND end_time = #{endTime,jdbcType=TIMESTAMP}


        AND limit_count = #{limitCount,jdbcType=VARCHAR}


        AND coupon_id = #{couponId,jdbcType=INTEGER}


        AND sort = #{sort,jdbcType=INTEGER}

mapper.select:
SELECT aag.id rid,aag.goods_id,aag.gmt_create,aag.gmt_modified,aag.limit_count,aag.coupon_id,aag.sort , sa.gmt_start, sa.gmt_end, ag.name goodsName, sag.special_price activityAmount , ag.sale_amount, (ag.sale_amount - ac.amount) endAmount, ac.amount couponPrice, ifnull(agu.goods_count, -1) goods_count , ag.goods_icon, ag.thumbnail_icon, ag.goods_url FROM af_activity_reservation_goods aag left join af_activity_reservation_goods_user agu on agu.goods_id = aag.id and agu.user_id = #{userId,jdbcType=INTEGER} and aag.id in ( select gu.goods_id from af_activity_reservation_goods_user gu where gu.user_id = #{userId,jdbcType=INTEGER} and gu.goods_count > 0 ) left join af_goods ag on ag.id = aag.goods_id left join af_seckill_activity_goods sag on sag.goods_id = aag.goods_id and sag.is_delete = 0 left join af_seckill_activity sa on sa.id = sag.activity_id left join af_coupon ac on ac.id = aag.coupon_id WHERE aag.is_delete = 0
AND aag.goods_id = #{goodsId,jdbcType=INTEGER}
and sa.id = #{activityId,jdbcType=INTEGER} and ag.status = 'PUBLISH' and sag.special_price != 0 GROUP BY aag.goods_id order by aag.sort DESC
