mapper.sql:
	WHERE is_delete = 0

        AND id  = #{rid,jdbcType=INTEGER}


        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND name = #{name,jdbcType=VARCHAR}


        AND position_code = #{positionCode,jdbcType=VARCHAR}


        AND image = #{image,jdbcType=VARCHAR}


        AND url = #{url,jdbcType=VARCHAR}


        AND url_type = #{urlType,jdbcType=VARCHAR}


        AND start_time = #{startTime,jdbcType=TIMESTAMP}


        AND end_time = #{endTime,jdbcType=TIMESTAMP}


        AND click_count = #{clickCount,jdbcType=INTEGER}


        AND day_count = #{dayCount,jdbcType=INTEGER}


        AND priority = #{priority,jdbcType=INTEGER}


        AND status = #{status,jdbcType=VARCHAR}


        AND remark = #{remark,jdbcType=VARCHAR}


        AND tag_conditions = #{tagConditions,jdbcType=VARCHAR}


        AND query_conditions = #{queryConditions,jdbcType=VARCHAR}

mapper.select:
select count(0) from af_advertise_user_tag b
where b.user_id =  #{userId}
and 1 = 1
${queryConditions}
