mapper.sql:
	WHERE is_delete = 0
    
        AND id  = #{id,jdbcType=INTEGER}
    
    
        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}
    
    
        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}
    
    
        AND gmt_apply = #{gmtApply,jdbcType=TIMESTAMP}
    
    
        AND user_id = #{userId,jdbcType=INTEGER}
    
    
        AND order_id = #{orderId,jdbcType=INTEGER}
    
    
        AND apply_no = #{applyNo,jdbcType=VARCHAR}
    
    
        AND status = #{status,jdbcType=VARCHAR}
    
    
        AND verify_remark = #{verifyRemark,jdbcType=VARCHAR}
    
    
        AND user_reason = #{userReason,jdbcType=VARCHAR}
    
    
        AND pic_vouchers = #{picVouchers,jdbcType=VARCHAR}
    
    
        AND goods_back_address = #{goodsBackAddress,jdbcType=VARCHAR}
    
    
        AND logistics_company = #{logisticsCompany,jdbcType=VARCHAR}
    
    
        AND logistics_no = #{logisticsNo,jdbcType=VARCHAR}
    
    
        AND remark = #{remark,jdbcType=VARCHAR}

mapper.select:
select apply_no from af_aftersale_apply WHERE #{endDate} >= gmt_create 
AND gmt_create >=#{startDate} 
and gmt_create is not null 
ORDER BY gmt_create DESC LIMIT 1;
