mapper.sql:
	WHERE is_delete = 0

        AND id  = #{rid,jdbcType=INTEGER}


        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND boluome_activity_id = #{boluomeActivityId,jdbcType=INTEGER}


        AND name = #{name,jdbcType=VARCHAR}


        AND icon_url = #{iconUrl,jdbcType=VARCHAR}


        AND type = #{type,jdbcType=VARCHAR}


        AND ref_id = #{refId,jdbcType=INTEGER}


        AND sort = #{sort,jdbcType=INTEGER}


        AND rule_json = #{ruleJson,jdbcType=INTEGER}


        AND logo = #{logo,jdbcType=VARCHAR}


        AND status = #{status,jdbcType=VARCHAR}

mapper.select:
SELECT DISTINCT t1.id AS shopId,1000 AS activityId,t1.`name`, CASE WHEN t2.status=1 THEN t1.icon_url ELSE t1.logo END AS image, t1.sort,t1.activityId AS rid FROM (SELECT s.id,s.`name`,s.`icon`,s.`type`,a.`id` AS activityId,a.`sort` ,a.`icon_url` ,a.`logo`,a.rule_json FROM af_boluome_activity_items a JOIN `af_shop` s ON a.`ref_id` = s.`id` AND a.`boluome_activity_id` =1000 ) t1 LEFT JOIN ( SELECT CASE tt.sec_type WHEN 'HUAFEI' THEN 'CHONGZHI' WHEN 'LIULIANG' THEN 'CHONGZHI' WHEN 'SUPGAME' THEN ( SELECT TYPE FROM af_sup_game g WHERE g.id = tt.goods_id ) ELSE tt.sec_type END otype,1 AS status FROM (
SELECT sec_type,1 AS STATUS ,goods_id FROM af_boluome_rebate t JOIN af_order o ON o.id = t.order_id WHERE t.user_id = #{userId} and o.is_delete = 0 AND t.is_delete = 0
AND o.gmt_rebated >= #{activityTime}
)tt LEFT JOIN af_sup_game g ON g.id = tt.goods_id GROUP BY otype ) t2 ON t1.rule_json = t2.otype ORDER BY `sort`
