mapper.sql:
	WHERE is_delete = 0

        AND id  = #{rid,jdbcType=INTEGER}


        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND boluome_activity_id = #{boluomeActivityId,jdbcType=INTEGER}


        AND name = #{name,jdbcType=VARCHAR}


        AND icon_url = #{iconUrl,jdbcType=VARCHAR}


        AND type = #{type,jdbcType=VARCHAR}


        AND ref_id = #{refId,jdbcType=INTEGER}


        AND sort = #{sort,jdbcType=INTEGER}


        AND rule_json = #{ruleJson,jdbcType=INTEGER}


        AND logo = #{logo,jdbcType=VARCHAR}


        AND status = #{status,jdbcType=VARCHAR}

mapper.select:
SELECT
FROM af_boluome_activity_items WHERE is_delete = 0 AND boluome_activity_id = 1000
AND ref_id = ( SELECT id FROM af_shop WHERE TYPE = ( SELECT CASE sec_type WHEN 'HUAFEI' THEN 'CHONGZHI' WHEN 'LIULIANG' THEN 'CHONGZHI' ELSE sec_type END AS type FROM af_order WHERE id = #{orderId} ) AND is_delete = 0 LIMIT 0 ,1 );
