mapper.update:
UPDATE af_boluome_activity_user_login


        gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},                


        user_id = #{userId,jdbcType=INTEGER},                


        user_name = #{userName,jdbcType=VARCHAR},                


        ref_user_id = #{refUserId,jdbcType=INTEGER},                


        ref_user_name = #{refUserName,jdbcType=VARCHAR}                


        boluome_activity_id = #{boluomeActivityId,jdbcType=INTEGER}


        binding_flag = #{bindingFlag,jdbcType=VARCHAR}


WHERE is_delete = 0 AND id = #{rid ,jdbcType=BIGINT}

mapper.select:
    SELECT
	count(*)
	FROM
		af_boluome_activity_user_login
	WHERE
		is_delete = 0
	AND ref_user_id = #{refUserId}
	AND boluome_activity_id = #{activityId}
