mapper.sql:
	WHERE is_delete = 0
    
        AND id  = #{rid,jdbcType=INTEGER}
    
    
        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}
    
    
        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}
    
    
        AND user_id = #{userId,jdbcType=INTEGER}
    
    
        AND first_order = #{firstOrder,jdbcType=INTEGER}
    
    
        AND order_id = #{orderId,jdbcType=INTEGER}
    
    
        AND rebate_amount = #{rebateAmount,jdbcType=DECIMAL}

mapper.select:
    SELECT s.id as shopId FROM af_order o 
	INNER JOIN af_shop s ON o.sec_type = s.type  and o.is_delete = s.is_delete = 0
	 id = #{orderId}
	LIMIT 0,1;

    SELECT s.`name` FROM af_boluome_rebate br 
	INNER JOIN af_order o on o.id = br.order_id 
	INNER JOIN af_shop s on s.type = o.sec_type 
	AND br.is_delete = o.is_delete = s.is_delete
	AND br.user_id = #{userId}
	;
