mapper.sql:
	WHERE is_delete = 0

        AND id  = #{rid,jdbcType=INTEGER}


        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND user_id = #{userId,jdbcType=INTEGER}


        AND first_order = #{firstOrder,jdbcType=INTEGER}


        AND order_id = #{orderId,jdbcType=INTEGER}


        AND rebate_amount = #{rebateAmount,jdbcType=DECIMAL}

mapper.select:
SELECT COUNT(1) FROM af_boluome_rebate WHERE user_id = #{userId}
AND is_delete = 0

		and gmt_create  >=  #{activityTime}

    	SELECT * FROM af_boluome_rebate WHERE user_id = #{userId} 
		AND is_delete = 0

    			and gmt_create  >=  #{startTime}

SELECT s.name AS consumeScene, tt.surpriseRebate,DATE_FORMAT(tt.consumeTime, '%Y-%m-%d %H:%i') AS consumeTime,tt.sceneRebate FROM af_shop s INNER JOIN ( SELECT t.surpriseRebate,t.consumeTime,t.sceneRebate,i.ref_id FROM ( SELECT ss.surpriseRebate,ss.consumeTime,ss.sceneRebate, CASE ss.otype WHEN 'SUPGAME' THEN g.type ELSE ss.otype END otype
FROM (
SELECT br.rebate_amount AS surpriseRebate ,o.gmt_create AS consumeTime,o.rebate_amount AS sceneRebate,o.goods_id, CASE o.sec_type WHEN 'HUAFEI' THEN 'CHONGZHI' WHEN 'LIULIANG' THEN 'CHONGZHI' ELSE o.sec_type END otype FROM af_order o LEFT JOIN af_boluome_rebate br ON o.id = br.order_id AND br.is_delete = 0 WHERE o.user_id = #{userId} AND o.`status` IN ('FINISHED','REBATED') AND o.`gmt_rebated` >= #{startTime} AND o.is_delete = 0 )ss LEFT JOIN af_sup_game g ON g.id = ss.goods_id
)t LEFT JOIN af_boluome_activity_items i ON t.otype = i.rule_json WHERE i.is_delete = 0 AND i.boluome_activity_id = 1000 AND i.type = 'ITEM' GROUP BY t.otype ) AS tt ON s.id = tt.ref_id AND s.is_delete = 0 AND s.`status`='O' ORDER BY consumeTime DESC , surpriseRebate DESC;

SELECT COUNT(1) FROM ( SELECT s.id AS shopId, tt.user_id FROM af_shop s INNER JOIN ( SELECT ss.surpriseRebate,ss.user_id,ss.order_id,ss.otype,ss.goods_id, CASE ss.otype WHEN 'SUPGAME' THEN g.type ELSE ss.otype END type FROM ( SELECT br.rebate_amount AS surpriseRebate, br.user_id,o.id order_id,o.goods_id, CASE o.sec_type WHEN 'HUAFEI' THEN 'CHONGZHI' WHEN 'LIULIANG' THEN 'CHONGZHI' ELSE o.sec_type END otype FROM af_boluome_rebate br INNER JOIN af_order o ON o.id = br.order_id AND o.is_delete = 0 AND br.is_delete = 0
and gmt_rebated >= #{activityTime}
)ss LEFT JOIN af_sup_game g ON g.id = ss.goods_id
) AS tt ON s.type = tt.otype AND s.is_delete = 0 GROUP BY s.`name` ,tt.user_id )t WHERE t.shopId = #{shopId} AND t.user_id = #{userId} LIMIT 0,1;

        select count(0) 
        from af_boluome_rebate 
        where is_delete = 0
        and user_id = #{userId}
        and first_order = #{firstOrder}

		and   gmt_create   >=     #{oneYuanTime}
