SELECT distinct ao.`id` orderId, ao.order_no orderNo, ao.order_type orderType, ao.shop_name shopName, ao.goods_name goodsName, ao.count count, ao.price_amount priceAmount, ao.sale_amount saleAmount, ao.actual_amount actualAmount, ao.borrow_amount borrowAmount, ao.bank_amount bankAmount, ao.pay_type payType, ao.gmt_pay gmtPay, ao.nper nper, ao.consignee consignee, ao.consignee_mobile consigneeMobile, ao.address consigneeAddress, aub.`card_number` bankCard, aub.`bank_name` bankName, ao.bank_id bankId FROM `af_order` ao LEFT JOIN `af_borrow` ab on ab.`order_id`= ao.`id` LEFT JOIN `af_borrow_bill` abb on abb.`borrow_id`= ab.`id` LEFT JOIN `af_user_bankcard` aub on aub.`user_id` = ao.`user_id` WHERE ab.`is_delete` = 0 AND abb.`is_delete` = 0 AND aub.`is_delete` = 0 AND aub.`is_main` = 'Y' AND aub.`status` = 'B' AND abb.id IN
#{item}
ORDER BY ao.`id`

SELECT ab.`id` borrowId, abb.id billId, abb.borrow_no borrowNo, ab.gmt_create gmtBorrow, abb.bill_year billYear, abb.bill_month billMonth, abb.nper nper, abb.bill_nper billNper, abb.bill_amount billAmount, abb.status status, abb.remark remark, abb.overdue_days overdueDays, abb.principle_amount principleAmount, abb.interest_amount interestAmount, abb.overdue_interest_amount overdueInterestAmount, abb.poundage_amount poundageAmount, abb.overdue_poundage_amount overduePoundageAmount, abbb.gmt_create repayDate, abb.gmt_pay_time lastRepayDay FROM `af_borrow` ab LEFT JOIN `af_borrow_bill` abb on abb.`borrow_id`= ab.`id` LEFT JOIN `af_repayment` abbb on abb.repayment_id=abbb.id WHERE ab.`is_delete`= 0 AND abb.`is_delete`= 0 AND abb.id IN
#{item}
AND ab.`order_id` = #{orderId} ORDER BY abb.bill_year DESC, abb.bill_month DESC

SELECT
	COALESCE (sum(bill_amount), 0)
FROM
	af_borrow_bill
WHERE
	is_delete = 0

	AND is_out = #{isOut}


	AND status = #{status}


	AND overdue_status = #{overdueStatus}


	AND gmt_out_day >= #{outDayStr} 
	and gmt_out_day < #{outDayEnd}

AND user_id = #{userId}

SELECT
	COALESCE (sum(bill_amount), 0) billAmount,
	COALESCE (sum(overdue_interest_amount), 0) overdueInterestAmount,
	gmt_out_day gmtOutDay
FROM
	af_borrow_bill
WHERE
	is_delete = 0

	AND is_out = #{isOut}


	AND status = #{status}


	AND overdue_status = #{overdueStatus}


	AND gmt_out_day >= #{outDayStr} 
	and gmt_out_day < #{outDayEnd}

AND user_id = #{userId}
GROUP BY
	gmt_out_day
ORDER BY
	gmt_out_day

