SELECT a.id rid, a.NAME name, a.bill_amount billAmount, a.bill_nper billNper, a.nper nper, c.gmt_pay payDate, a.gmt_out_day gmtOutDay, a.gmt_pay_time gmtPayTime FROM `af_borrow_bill` a LEFT JOIN `af_borrow` b on a.`borrow_id`= b.`id` LEFT JOIN `af_order` c on b.`order_id`= c.id where a.`is_delete`= 0
AND a.is_out = #{isOut}
AND a.status = #{status}
AND a.overdue_status = #{overdueStatus}
AND gmt_out_day >= #{outDayStr} and gmt_out_day < #{outDayEnd}
AND a.`user_id` = #{userId} ORDER BY c.`gmt_pay`

