mapper.insert:
INSERT INTO af_borrow_cash(gmt_create , gmt_modified , borrow_no , user_id , type, amount , status , card_number , card_name , poundage , rate_amount , latitude , longitude , province ,arrival_amount, city , county, address, poundage_rate, base_bank_rate
,majiabao_name
,risk_daily_rate
) VALUES ( NOW(), NOW(), #{borrowNo}, #{userId}, #{type}, #{amount}, #{status}, #{cardNumber}, #{cardName}, #{poundage}, #{rateAmount}, #{latitude}, #{longitude}, #{province}, #{arrivalAmount}, #{city}, #{county}, #{address}, #{poundageRate}, #{baseBankRate}
,#{majiabaoName}
,#{riskDailyRate}
)

mapper.update:
UPDATE af_borrow_cash SET gmt_modified = NOW()

	,borrow_no=#{borrowNo}


	,user_id=#{userId}


	,type=#{type}


	,amount=#{amount}


	,status=#{status}


	,remark=#{remark}


	,card_number=#{cardNumber}


	,card_name=#{cardName}


	,overdue_day=#{overdueDay}


	,overdue_amount=#{overdueAmount}


	,arrival_amount=#{arrivalAmount}


	,gmt_arrival=#{gmtArrival}


	,review_name=#{reviewName}


	,review_user_name=#{reviewUserName}


	,review_details=#{reviewDetails}


	,gmt_review=#{gmtReview}


	,gmt_close=#{gmtClose}


	,poundage=#{poundage}


	,overdue_status=#{overdueStatus}


	,review_status=#{reviewStatus}


	,rate_amount=#{rateAmount}


	,repay_amount=#{repayAmount}


	,rish_order_no=#{rishOrderNo}



	,gmt_plan_repayment=#{gmtPlanRepayment}


	,sum_rate=#{sumRate}


	,sum_overdue=#{sumOverdue}


	,sum_jfb=#{sumJfb}


	,sum_rebate=#{sumRebate}


	,sum_renewal_poundage=#{sumRenewalPoundage}


	,renewal_num=#{renewalNum}


	,majiabao_name=#{majiabaoName}


	,finish_date=#{finishDate}


	,risk_daily_rate=#{riskDailyRate}

WHERE id = #{rid}

mapper.select:
select borrow_no
from af_borrow_cash WHERE borrow_no LIKE CONCAT(#{orderNoPre},"%")
ORDER BY id DESC LIMIT 1;

SELECT

FROM
af_borrow_cash
WHERE
is_delete = 0
AND
user_id = #{userId}
and   gmt_create  >=  #{startTime}  
and   gmt_create  <=  #{endTime}  
ORDER BY gmt_create DESC LIMIT 1;

mapper.update:
UPDATE af_borrow_cash SET gmt_modified = NOW()

	,status=#{status}


	,rate_amount=#{rateAmount}


	,overdue_amount=#{overdueAmount}


	,sum_rate=#{sumRate}


	,sum_overdue=#{sumOverdue}

WHERE id=#{rid}

UPDATE af_borrow_legal_order_cash SET gmt_modified = NOW()

	,status=#{status}


	,overdue_amount=#{overdueAmount}


	,sum_repaid_overdue=#{sumRepaidOverdue}

WHERE id=#{rid}

UPDATE af_borrow_cash SET gmt_modified = NOW()

	,rd_before_overdue_status=#{overdueStatus}

WHERE id=#{rid}

UPDATE af_borrow_cash SET gmt_modified = NOW()
	,au_amount=#{auAmount}
WHERE id=#{rid}

mapper.select:
SELECT
FROM af_borrow_cash WHERE is_delete = 0 AND user_id = #{userId} and status not in('APPLY','TRANSEDING','WAITTRANSED') ORDER BY gmt_create DESC
limit #{rows}
