mapper.insert:
		INSERT INTO af_borrow_log(gmt_create , creator , type)
		VALUES (
    		NOW(),
    		#{creator},
    		#{type}
		)

mapper.update:
		UPDATE af_borrow_log SET gmt_modified = NOW()

    			,is_delete=#{isDelete}


    			,creator=#{creator}


    			,type=#{type}

		WHERE id = #{rid}
