mapper.select:
SELECT
DISTINCT ref_id
FROM af_business_access_records
WHERE user_id = #{userId}
AND is_delete = 0
AND remark='sign'
AND ref_type = #{refType}
AND gmt_create   <   CAST(CAST(SYSDATE()AS DATE)AS DATETIME)
AND gmt_create   >=   #{beginTime}

SELECT
COUNT(DISTINCT ref_id)
FROM af_business_access_records
WHERE user_id = #{userId}
AND is_delete = 0

AND ref_id NOT IN 

	#{id}


AND gmt_create   >=   CAST(CAST(SYSDATE()AS DATE)AS DATETIME);

if.foreach:
(SELECT
id
FROM af_business_access_records
WHERE user_id = #{userId}
AND is_delete = 0
AND ref_id = #{id}
AND gmt_create   >=   CAST(CAST(SYSDATE()AS DATE)AS DATETIME)
ORDER BY gmt_create asc
limit 1
)

