mapper.update:
		UPDATE af_cash_log SET gmt_modified = NOW()

    			,is_delete=#{isDelete}


    			,gmt_modifier=#{gmtModifier}


    			,creator=#{creator}


    			,status=#{status}


    			,cash_record_id=#{cashRecordId}


    			,user_id=#{userId}


    			,content=#{content}


    			,gmt_auth=#{gmtAuth}


    			,auth_user=#{authUser}


    			,gmt_deliver=#{gmtDeliver}


    			,deliver_user=#{deliverUser}

		WHERE id = #{rid}
