mapper.sql:
WHERE is_delete = 0

    AND id = #{rid,jdbcType=INTEGER}


    AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


    AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


    AND scene_type = #{sceneType,jdbcType=VARCHAR}


    AND installment_status = #{installmentStatus,jdbcType=VARCHAR}


    AND cppay_status = #{cppayStatus,jdbcType=VARCHAR}


    AND wxpay_status = #{wxpayStatus,jdbcType=VARCHAR}


    AND bankpay_status = #{bankpayStatus,jdbcType=VARCHAR}


    AND scene_second_type = #{sceneSecondType,jdbcType=VARCHAR}


    AND name = #{name,jdbcType=VARCHAR}


    AND name = #{credit_status,jdbcType=VARCHAR}


    AND alipay_status = #{alipayStatus,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM af_checkout_counter
WHERE is_delete = 0 AND scene_type=#{type ,jdbcType=VARCHAR}

    and scene_second_type= #{secType ,jdbcType=VARCHAR}

LIMIT 0,1
