mapper.select:
SELECT
FROM af_goods g WHERE g.is_delete=0 and g.status = 'PUBLISH'
and g.primary_category_id=#{categoryId}
and exists(select 1 from af_category c where c.is_delete=0 and c.level=1 and g.primary_category_id=c.id)
AND g.name LIKE CONCAT('%',#{keywords}, '%')
AND (g.sale_amount-g.rebate_amount)>=#{minAmount}
AND (g.sale_amount-g.rebate_amount) <= #{maxAmount}
AND g.source = #{source}
AND id <= 110552
AND id > 10552
order by g.gmt_modified desc

mapper.update:
UPDATE
	af_goods
SET
	gmt_modified = NOW(),
	modifier = 'system',
	subscribe = #{status}

		 ,status = #{publishStatus}

WHERE
	num_id = #{numId}

UPDATE
	af_goods
SET
	gmt_modified = NOW()
	,modifier = 'system'

		,name = #{name}




		,price_amount = #{priceAmount}


		,sale_amount = #{saleAmount}

WHERE
	num_id = #{numId}

mapper.select:
select
FROM af_goods g inner join ( SELECT goods_id FROM af_subject_goods WHERE subject_id =( SELECT min(id) id FROM af_subject WHERE parent_id = #{parentId} AND LEVEL = 2 ) AND parent_subject_id = #{parentId} and is_delete = 0
)as asg on asg.goods_id = g.id and g.is_delete = 0 and g.status = 'PUBLISH'

SELECT

FROM af_goods
WHERE is_delete = 0
AND STATUS = 'PUBLISH'
AND primary_category_id = #{primaryCategoryId}
AND category_id = #{categoryId}

SELECT
FROM af_goods WHERE is_delete = 0 AND `status` = 'PUBLISH' AND source = 'SELFSUPPORT'
AND name LIKE CONCAT('%',#{keyword},'%')
order by ${sortword}
${sort}
