mapper.select:
SELECT

FROM
	af_goods
WHERE
	is_delete=0  and status = 'PUBLISH'

		and category_id = #{categoryId}


		AND name LIKE CONCAT('%',#{keywords}, '%')


		AND (sale_amount-rebate_amount)>=#{minAmount}


		AND (sale_amount-rebate_amount)  <=   #{maxAmount}

mapper.update:
UPDATE
	af_goods
SET
	gmt_modified = NOW()
	,modifier = 'system'

		,name = #{name}


		,goods_icon = #{goodsIcon}


		,price_amount = #{priceAmount}


		,sale_amount = #{saleAmount}

WHERE
	num_id = #{numId}
