mapper.sql:
	WHERE is_delete = 0

        AND id  = #{rid,jdbcType=INTEGER}


        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND goods_id = #{goodsId,jdbcType=INTEGER}


        AND sort = #{sort,jdbcType=INTEGER}


        AND start_time = #{startTime,jdbcType=TIMESTAMP}


        AND end_time = #{endTime,jdbcType=TIMESTAMP}


        AND already_count = #{alreadyCount,jdbcType=INTEGER}

mapper.select:
SELECT

  FROM af_goods_double_eggs
WHERE is_delete = 0 AND goods_id = #{goodsId} limit 0,1;

SELECT

  FROM af_goods_double_eggs
WHERE is_delete = 0 AND id = #{doubleGoodsId};

SELECT start_time FROM af_goods_double_eggs 
	WHERE is_delete = 0 
	and start_time >= #{startTime}
GROUP BY start_time ORDER BY start_time ;

SELECT de.id AS double_goods_id, CASE WHEN CURRENT_DATE > date(de.start_time) THEN '4' WHEN now() > de.start_time THEN '2' ELSE '0' END AS STATUS, de.goods_id, g.stock_count,
CASE WHEN( g.stock_count - de.already_count )<0 THEN 0 ELSE g.stock_count - de.already_count END AS count,
g.price_amount AS sale_amount, g.sale_amount AS real_amount, g.`name` AS goods_name, g.goods_icon, g.thumbnail_icon, g.goods_url FROM af_goods_double_eggs de INNER JOIN af_goods g ON g.id = de.goods_id INNER JOIN af_activity a ON a.id = de.activity_id WHERE de.is_delete = 0 AND g.is_delete = 0 AND a.is_delete = 0 AND a.`status` = 2 AND a.`name` = #{tag} AND DATE(de.start_time) = DATE(#{startDate}) ORDER BY de.sort;

SELECT de.goods_id AS id, de.activity_id AS activity_id, ( SELECT count(*) FROM af_goods_double_eggs_user u WHERE u.double_eggs_id = de.id )+de.already_count AS order_number,
CASE WHEN ( SELECT count(*) FROM af_goods_double_eggs_user u WHERE u.double_eggs_id = de.id AND u.user_id = #{userId} ) > 0 THEN 'Y' ELSE 'N' END
as is_order FROM af_goods_double_eggs de WHERE de.reserve = 'Main' ORDER BY de.goods_id;

SELECT de.id AS double_goods_id, CASE WHEN NOW() > de.start_time AND NOW() < de.end_time THEN '2' WHEN now() < de.start_time THEN '0' ELSE '4' END AS STATUS, de.goods_id, g.stock_count,
CASE WHEN( g.stock_count - de.already_count )<0 THEN 0 ELSE g.stock_count - de.already_count END AS count,
g.price_amount AS sale_amount, g.sale_amount AS real_amount, g.`name` AS goods_name, g.goods_icon, g.thumbnail_icon, g.goods_url FROM af_goods_double_eggs de INNER JOIN af_goods g ON g.id = de.goods_id INNER JOIN af_activity a ON a.id = de.activity_id WHERE de.is_delete = 0 AND g.is_delete = 0 AND a.is_delete = 0 AND a.`status` = 2 AND de.activity_id = #{activityId} ORDER BY de.sort;
