mapper.sql:
	WHERE is_delete = 0
    
        AND id  = #{rid,jdbcType=INTEGER}
    
    
        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}
    
    
        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}
    
    
        AND creator = #{creator,jdbcType=VARCHAR}
    
    
        AND modifier = #{modifier,jdbcType=VARCHAR}
    
    
        AND interest_reduce_scheme_id = #{interestReduceSchemeId,jdbcType=INTEGER}
    
    
        AND goods_id = #{goodsId,jdbcType=INTEGER}
    
    
        AND interest_reduce_rules__id = #{interestReduceRulesId,jdbcType=INTEGER}

mapper.select:
    SELECT r.nper2,r.nper3,r.nper6,r.nper9,r.nper12 FROM af_interest_reduce_rules r 
	INNER JOIN af_interest_reduce_scheme s on r.id = s.interest_reduce_id
	INNER JOIN af_interest_reduce_goods g on g.interest_reduce_scheme_id = s.id
	WHERE r.is_delete = 0 
	AND g.is_delete = 0 
	AND s.is_delete = 0 
	AND s.rule_type = #{ruleType}
	AND g.goods_id = #{goodsId}
	AND s.is_open = 'Y';

    SELECT s.descr FROM af_interest_reduce_scheme s
	INNER JOIN af_interest_reduce_goods g on g.interest_reduce_scheme_id = s.id
	WHERE
	g.is_delete = 0 
	AND s.is_delete = 0 
	AND s.rule_type = #{ruleType}
	AND g.goods_id = #{goodsId}
	AND s.is_open = 'Y';
