mapper.sql:
	WHERE is_delete = 0

        AND id  = #{rid,jdbcType=INTEGER}


        AND user_id = #{userId,jdbcType=INTEGER}


        AND loan_id = #{loanId,jdbcType=INTEGER}


        AND loan_no = #{loanNo,jdbcType=VARCHAR}


        AND prd_type = #{prdType,jdbcType=VARCHAR}


        AND amount = #{amount,jdbcType=DECIMAL}


        AND periods = #{periods,jdbcType=INTEGER}


        AND nper = #{nper,jdbcType=INTEGER}


        AND status = #{status,jdbcType=VARCHAR}


        AND repay_id = #{repayId,jdbcType=INTEGER}


        AND remark = #{remark,jdbcType=VARCHAR}


        AND overdue_status = #{overdueStatus,jdbcType=VARCHAR}


        AND overdue_days = #{overdueDays,jdbcType=INTEGER}


        AND interest_fee = #{interestFee,jdbcType=DECIMAL}


        AND service_fee = #{serviceFee,jdbcType=DECIMAL}


        AND overdue_amount = #{overdueAmount,jdbcType=DECIMAL}


        AND repay_amount = #{repayAmount,jdbcType=DECIMAL}


        AND pre_repay_status = #{preRepayStatus,jdbcType=VARCHAR}


        AND repaid_service_fee = #{repaidServiceFee,jdbcType=DECIMAL}


        AND repaid_interest_fee = #{repaidInterestFee,jdbcType=DECIMAL}


        AND repaid_overdue_amount = #{repaidOverdueAmount,jdbcType=DECIMAL}


        AND gmt_last_repay = #{gmtLastRepay,jdbcType=TIMESTAMP}


        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND gmt_plan_repay = #{gmtPlanRepay,jdbcType=TIMESTAMP}
