mapper.update:
		UPDATE af_loan_supermarket SET gmt_modified = NOW()
			
    			,lsm_no=#{lsmNo}
			
			
    			,icon_url=#{iconUrl}
			
			
    			,create_user_id=#{createUserId}
			
			
    			,modify_user_id=#{modifyUserId}
			
			
    			,lsm_name=#{lsmName}
			
			
    			,lsm_intro=#{lsmIntro}
			
			
    			,link_url=#{linkUrl}
			
			
    			,label=#{label}
			
			
    			,order_no=#{orderNo}
			
			
    			,status=#{status}
			
			
    			,is_delete=#{isDelete}
			
		WHERE id = #{id}
