mapper.insert:
INSERT INTO af_order( gmt_create , gmt_modified ,
user_id ,
order_no ,
user_coupon_id,
pay_trade_no,
order_type , goods_id , open_id , goods_name , goods_icon , count , price_amount , sale_amount , actual_amount , shop_name , rebate_amount , commission_amount, mobile, bank_id
,gmt_pay_end
,sec_type
,third_order_no
,third_detail_url
) VALUES (
#{gmtCreate} , #{gmtCreate} ,
NOW(), NOW(),
#{userId},
#{orderNo},
#{userCouponId},
#{payTradeNo},
#{orderType}, #{goodsId}, #{openId}, #{goodsName}, #{goodsIcon}, #{count}, #{priceAmount}, #{saleAmount}, #{actualAmount}, #{shopName}, #{rebateAmount}, #{commissionAmount}, #{mobile}, #{bankId}
,#{gmtPayEnd}
,#{secType}
,#{thirdOrderNo}
,#{thirdDetailUrl}
)

