,gmt_pay_end
,sec_type
,third_order_no
,third_detail_url
) VALUES (
#{gmtCreate} , #{gmtCreate} ,
NOW(), NOW(),
#{userId},
#{orderNo},
#{userCouponId},
#{payTradeNo},
#{orderType}, #{goodsId}, #{openId}, #{goodsName}, #{goodsIcon}, #{count}, #{priceAmount}, #{saleAmount}, #{actualAmount}, #{shopName}, #{rebateAmount}, #{mobile}, #{bankId}
,#{gmtPayEnd}
,#{secType}
,#{thirdOrderNo}
,#{thirdDetailUrl}
)

