mapper.update:
		UPDATE af_order_refund SET gmt_modified = NOW()
			
    			,amount=#{amount}
			
			
    			,user_id=#{userId}
			
			
    			,order_id=#{orderId}
			
			
    			,order_no=#{orderNo}
			
			
    			,status=#{status}
			
			
    			,type=#{type}
			
			
    			,account_number=#{accountNumber}
			
			
    			,account_name=#{accountName}
			
			
				,content = #{content}
			
			
				,resource = #{resource}
			
		WHERE id = #{rid}
