mapper.update:
UPDATE af_recommend_user
set is_loan = 1,
loan_user_count =1,
prize_money = #{prize_money},
loan_time = #{loan_time}
where id = #{id}

mapper.select:
select
a.*,b.user_name
from af_recommend_user a  left join af_user b on a.user_id = b.id  where parentId = #{userId} and origin = 0 ORDER BY gmt_create desc limit #{pageIndex},#{pageSize}

select b.user_name, user_id,SUM(a.prize_money) sum_money from af_recommend_user a left join af_user b on a.parentId = b.id
where a.loan_time >= #{startTime} and a.loan_time <= #{endTime}
GROUP BY a.parentId ORDER BY SUM(a.prize_money) desc LIMIT 20;
