mapper.sql:
	WHERE is_delete = 0

        AND id  = #{rid,jdbcType=INTEGER}


        AND user_id = #{userId,jdbcType=INTEGER}


        AND amount = #{amount,jdbcType=DECIMAL}


        AND is_withdraw = #{isWithdraw,jdbcType=INTEGER}


        AND gmt_withdraw = #{gmtWithdraw,jdbcType=TIMESTAMP}


        AND gmt_withdraw  >=  #{gmtWithdrawStart}


        AND gmt_withdraw  <=  #{gmtWithdrawEnd}


        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND creator = #{creator,jdbcType=VARCHAR}


        AND modifier = #{modifier,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM af_red_packet_total
WHERE is_delete = 0 AND is_withdraw = 1 AND user_id = #{userId}
ORDER BY gmt_withdraw DESC

    LIMIT #{queryNum}

mapper.update:
UPDATE af_red_packet_total
SET amount = amount + #{openAmount}, modifier = #{modifier}
WHERE id = #{id}

mapper.select:
SELECT IFNULL(SUM(amount), 0)
FROM af_red_packet_total
WHERE is_delete = 0 AND is_withdraw = 1 AND gmt_withdraw  >=  #{gmtWithdrawStart}
  AND gmt_withdraw  <=  #{gmtWithdrawEnd}
