mapper.select:
SELECT
FROM af_resource WHERE is_delete = 0 AND type IN
#{item}

SELECT

FROM
af_resource
WHERE
is_delete = 0 AND type = #{type} AND sec_type = #{secType}
and value_4 ='O';

mapper.update:
		UPDATE af_resource SET gmt_modified = NOW()

    			,modifier=#{modifier}


