    			,type_desc=#{typeDesc}


    			,name=#{name}


    			,value=#{value}


    			,description=#{description}


    			,sec_type=#{secType}


    			,value_1=#{value1}


    			,value_2=#{value2}


    			,value_3=#{value3}


    			,value_4=#{value4}


    			,sort=#{sort}


