mapper.sql:
	WHERE is_delete = 0

        AND id  = #{rid,jdbcType=INTEGER}


        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND user_id = #{userId,jdbcType=INTEGER}


        AND task_id = #{taskId,jdbcType=INTEGER}


        AND coin_amount = #{coinAmount,jdbcType=INTEGER}


        AND cash_amount = #{cashAmount,jdbcType=DECIMAL}


        AND coupon_id = #{couponId,jdbcType=INTEGER}


        AND status = #{status,jdbcType=INTEGER}

mapper.select:
SELECT

FROM af_task_user
WHERE is_delete = 0
AND user_id = #{userId}
AND task_id = #{taskId}
AND date_format(gmt_create,'%Y-%m-%d') = date_format(now(),'%Y-%m-%d')

mapper.update:
UPDATE af_task_user


        gmt_modified = #{gmtModified,jdbcType=TIMESTAMP},


        coin_amount = #{coinAmount,jdbcType=INTEGER},


        cash_amount = #{cashAmount,jdbcType=DECIMAL},


        coupon_id = #{couponId,jdbcType=INTEGER}


        status = #{status,jdbcType=INTEGER}


WHERE is_delete = 0 AND user_id = #{userId ,jdbcType=BIGINT} AND task_id = #{taskId ,jdbcType=BIGINT}
AND date_format(gmt_create,'%Y-%m-%d') = date_format(now(),'%Y-%m-%d')

UPDATE af_task_user


        gmt_modified = #{gmtModified,jdbcType=TIMESTAMP},


        coin_amount = #{coinAmount,jdbcType=INTEGER},


        cash_amount = #{cashAmount,jdbcType=DECIMAL},


        coupon_id = #{couponId,jdbcType=INTEGER}


        status = #{status,jdbcType=INTEGER}


WHERE is_delete = 0 AND user_id = #{userId ,jdbcType=BIGINT} AND task_id = #{taskId ,jdbcType=BIGINT}
