mapper.sql:
WHERE is_delete = 0

	AND order_id = #{orderId,jdbcType=INTEGER}


	AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


	AND business_id = #{businessId,jdbcType=INTEGER}


	AND refund_type = #{refundType,jdbcType=VARCHAR}


	AND status = #{status,jdbcType=VARCHAR}

mapper.select:
select count(a.id) count,sum(a.actual_amount) money from af_order a left join af_user b on a.user_id =b.id left join af_trade_order c on a.id = c.order_id where c.business_id = #{businessId} and a.status='PAID'
and a.gmt_create >= #{startOfDate}
and a.gmt_create <= #{endOfDate}

select distinct date_format(a.gmt_create, '%Y-%m-%d')
from af_order a left join af_user b
on a.user_id =b.id
left join af_trade_order c on a.id = c.order_id
where c.business_id = #{businessId}

	and a.gmt_create    >=  
	#{startOfDate}


	and a.gmt_create    <=  
	#{endOfDate}


	and a.status = #{orderStatus}

order by a.gmt_create desc

select
a.id,a.order_no,a.gmt_create,a.actual_amount,b.user_name,a.status
orderStatus,c.status withDrawStatus from
af_order a left join af_user b
on a.user_id =b.id
left join af_trade_order c on a.id = c.order_id
where c.business_id = #{businessId}

	and a.gmt_create    >=  
	#{startOfDate}


	and a.gmt_create    <=  
	#{endOfDate}


	and a.status = #{orderStatus}

order by a.gmt_create desc

select distinct date_format(d.gmt_create, '%Y-%m-%d')
from af_order a left join af_user b on a.user_id =b.id
left join af_trade_order c on a.id = c.order_id
left join af_order_refund d on d.order_id = a.id
where c.business_id = #{businessId}

	and c.status = #{refundStatus}


	and d.gmt_create    >=  
	#{startDate}


	and d.gmt_create    <=  
	#{endDate}

order by d.gmt_create desc

select a.id,a.order_no,d.gmt_create,d.amount actualAmount,b.user_name,c.status withDrawStatus from af_order a left join af_user b on a.user_id =b.id left join af_trade_order c on a.id = c.order_id left join af_order_refund d on d.order_id = a.id where c.business_id = #{businessId}
and c.status = #{refundStatus}
and d.gmt_create >= #{startDate}
and d.gmt_create <= #{endDate}
order by d.gmt_create desc
