mapper.update:
UPDATE
	af_user
SET
	gmt_modified = NOW()

	,user_name = #{userName}


	,password = #{password}


	,salt = #{salt}


	,gender = #{gender}


	,nick = #{nick}


	,avatar = #{avatar}


	,real_name = #{realName}


	,mobile = #{mobile}


	,email = #{email}


	,birthday = #{birthday}


	,province = #{province}


	,city = #{city}


	,county = #{county}


	,address = #{address}


	,recommend_id = #{recommendId}


	,fail_count = #{failCount}


	,recommend_code = #{recommendCode}

WHERE
	id = #{rid}
