mapper.update:
UPDATE af_user_amount SET gmt_modified = NOW()
	,status=#{status}
WHERE source_id = #{sourceId} and biz_type =#{bizType}

mapper.select:
SELECT  from 
	af_user_amount
WHERE
	user_id = #{userId}
	and biz_type = #{biz_type}
	and status != 0
ORDER BY
	gmt_create desc
LIMIT #{begin},#{pageSize}
