mapper.insert:
		INSERT INTO af_user_auth(gmt_modified , user_id )
		VALUES (
    		NOW(),
    		#{userId}
		)

mapper.update:
		UPDATE af_user_auth SET gmt_modified = NOW()

    			,zm_status=#{zmStatus}


    			,gmt_zm=#{gmtZm}


    			,zm_score=#{zmScore}


    			,isv_status=#{isvStatus}


    			,gmt_isv=#{gmtIsv}


    			,isv_score=#{isvScore}


    			,td_status=#{tdStatus}


    			,gmt_td=#{gmtTd}


    			,td_core=#{tdCore}


    			,faces_status=#{facesStatus}


    			,similar_degree=#{similarDegree}


    			,mobile_status=#{mobileStatus}


    			,bankcard_status=#{bankcardStatus}


    			,teldir_status=#{teldirStatus}


    			,yd_status=#{ydStatus}

		WHERE user_id = #{userId}
