mapper.insert:
		INSERT INTO af_user_auth(gmt_modified , user_id )
		VALUES (
    		NOW(),
    		#{userId}
		)

mapper.update:
		UPDATE af_user_auth SET gmt_modified = NOW()

    			,zm_status=#{zmStatus}


    			,gmt_zm=#{gmtZm}


    			,zm_score=#{zmScore}


    			,ivs_status=#{ivsStatus}


    			,gmt_ivs=#{gmtIvs}


    			,ivs_score=#{ivsScore}


    			,realname_status=#{realnameStatus}


    			,gmt_realname=#{gmtRealname}


    			,realname_score=#{realnameScore}


    			,faces_status=#{facesStatus}


    			,similar_degree=#{similarDegree}


    			,mobile_status=#{mobileStatus}


    			,bankcard_status=#{bankcardStatus}


    			,teldir_status=#{teldirStatus}


    			,yd_status=#{ydStatus}


    			,watchlist_status=#{watchlistStatus}


    			,gmt_watchlist=#{gmtWatchlist}


    			,watchlist_score=#{watchlistScore}

		WHERE user_id = #{userId}
