mapper.select:
SELECT
FROM af_user_coupon u LEFT JOIN af_coupon c ON u.coupon_id = c.id WHERE c.is_delete = 0 AND u.is_delete = 0 AND u.user_id = #{userId}
and u.status = #{status}
ORDER BY u.gmt_end

SELECT
FROM af_user_coupon u LEFT JOIN af_coupon c ON u.coupon_id = c.id WHERE c.is_delete = 0 AND u.is_delete = 0 AND u.user_id = #{userId}
and u.status = #{status}
ORDER BY u.gmt_end

SELECT CASE WHEN 48 > TIMESTAMPDIFF(HOUR, NOW(), uc.gmt_end) THEN 'Y' ELSE 'N' END AS will_expire_status, uc.id rid, c.limit_amount, c.amount, c.name, c.use_rule, c.type, uc.status, uc.gmt_start, uc.gmt_end ,c.use_range,uc.coupon_id FROM af_user_coupon uc LEFT JOIN af_coupon c ON uc.coupon_id = c.id WHERE uc.user_id = #{userId} AND c.type = 'FULLVOUCHER' AND uc.status = 'NOUSE' AND uc.is_delete = 0 AND c.is_delete = 0 AND (NOW() BETWEEN uc.gmt_start AND uc.gmt_end) AND #{amount} >= c.limit_amount AND c.is_global = 1
AND c.activity_id = #{activityId}
AND c.activity_type = #{activityType} ORDER BY c.amount DESC LIMIT 50
