mapper.select:
SELECT

FROM af_view_asset_borrow_cash
WHERE 1=1

    AND type = #{type,jdbcType=VARCHAR}


    AND gmt_create  >=  #{gmtCreateStart,jdbcType=TIMESTAMP}


    AND gmt_create  <=  #{gmtCreateEnd,jdbcType=TIMESTAMP}


    AND borrow_cash_id  >  #{minBorrowCashId}

    order by borrow_cash_id asc

   limit #{limitNums}

SELECT

FROM af_view_asset_borrow_cash
WHERE 1=1

    AND type = #{type,jdbcType=VARCHAR}


    AND gmt_create  >=  #{gmtCreateStart,jdbcType=TIMESTAMP}


    AND gmt_create  <=  #{gmtCreateEnd,jdbcType=TIMESTAMP}


    AND borrow_cash_id  >  #{minBorrowCashId}

    order by borrow_cash_id asc

   limit 1

 SELECT
 	IFNULL(SUM(amount),0)
 FROM 
		af_view_asset_borrow_cash
