mapper.sql:
	WHERE is_delete = 0

        AND id  = #{rid,jdbcType=INTEGER}


        AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND userid = #{userid,jdbcType=INTEGER}


        AND goodspriceid = #{goodspriceid,jdbcType=INTEGER}


        AND isbuy = #{isbuy,jdbcType=INTEGER}


        AND cutcount = #{cutcount,jdbcType=INTEGER}


        AND cutprice = #{cutprice,jdbcType=DECIMAL}


        AND gmt_completetime = #{gmtCompletetime,jdbcType=TIMESTAMP}


        AND completecount = #{completecount,jdbcType=INTEGER}

mapper.update:
UPDATE af_de_user_goods
set gmt_modified = sysdate(), isbuy = #{isbuy,jdbcType=INTEGER}
WHERE is_delete = 0 AND id = #{rid ,jdbcType=BIGINT}
