    			,lsm_name=#{lsmName}


    			,lsm_intro=#{lsmIntro}


    			,link_url=#{linkUrl}


    			,label=#{label}


    			,order_no=#{orderNo}


    			,status=#{status}


    			,is_delete=#{isDelete}

		WHERE id = #{id}
